char *WebHomePage =  
"\n<!--\nMatthew Francis-Landau\nmatthew@matthewfl.com\nTeam 687 The Nerd Herd\n-->\n<html>\n  <head>\n    <title>Team 687 Nerd Herd scouting</title>\n    <link type=\"text/css\" href=\"/style\" rel=\"stylesheet\" />\n    <script src=\"/js\"></script>\n  </head>\n  <body>\n    <div class=\"container_12\">\n      <div class=\"grid_9 pull_1 nh_header \">\n	Team 687 Nerd Herd\n      </div>\n      <div class=\"clear\"> </div>\n      <div class=\"grid_3\" id=\"side_bar\">\n	<!-- side bar -->\n	Mode: <select id=\"mode\">\n	  <option value=\"chose\">Chose a Mode</option>\n	  <option value=\"scoreMatch\">Scoring Match</option>\n	  <option value=\"teamList\">Team Info</option>\n	  <option value=\"enterList\">Enter Match list</option>\n	</select>\n      </div>\n      <div class=\"grid_9\" id=\"contnet\">\n	<div id=\"requireJS\"><h1>You need to enable JavaScript for the Scouting program to work</h1></div>\n	<div id=\"welcomeAction\" class=\"content_action\">Chose an Mode from the list on the left</div>\n	<div id=\"teamInfo\" class=\"content_action\">\n	  Teams: <select id=\"teamsList\">\n	    </select><br>\n	  <div id=\"teamInfoArea\" class=\"grid_9 alpha omega\" > </div>\n	</div>\n	<div id=\"enterList\" class=\"content_action\">\n	  <textarea rows=\"15\" cols=\"80\" id=\"enterMatches\"></textarea><br>\n	  <button id=\"enterSave\">Save</button>\n	</div>\n	<div id=\"matchScouting\" class=\"content_action\">\n	  <div class=\"grid_9 alpha omega\">\n	    <!-- top drop down for match scoring -->\n	    Match: <input id=\"matchNum\" class=\"sni\"/>\n	    Team: <select id=\"matchTeam\">\n	      <option value=\"red1\" style=\"color: #FF0000;\" id=\"red1\">red1</option>\n	      <option value=\"red2\" style=\"color: #FF0000;\" id=\"red2\">red2</option>\n	      <option value=\"red3\" style=\"color: #FF0000;\" id=\"red3\">red3</option>\n	      <option value=\"blue1\" style=\"color: #0000FF;\" id=\"blue1\">blue1</option>\n	      <option value=\"blue2\" style=\"color: #0000FF;\" id=\"blue2\">blue2</option>\n	      <option value=\"blue3\" style=\"color: #0000FF;\" id=\"blue3\">blue3</option>\n	    </select>\n	  </div>\n	  <div class=\"clear\"> </div>\n	  <div class=\"alpha grid_4\">Autonomous</div><div class=\"omega grid_4\">Teleoperated</div>\n	  <div class=\"clear\"> </div>\n	  <div class=\"grid_4 alpha\">\n	    Attempted: <input id=\"matchAutoScoreAtmp\" value=\"0\" class=\"sni\"/><br>\n	    Made: <input id=\"matchAutoScoreMade\" value=\"0\" class=\"sni\"/><br>\n	    Start Location: <select id=\"matchAutoStart\">\n	      <option value=\"1\">Near</option>\n	      <option value=\"2\">Middle</option>\n	      <option value=\"3\">Far</option>\n	    </select><br>\n	  </div>\n	  <div class=\"grid_4 push_1 omega\">\n	    Attempted: <input id=\"matchTelScoreAtmp\" value=\"0\" class=\"sni\" /><br>\n	    Made: <input id=\"matchTelScoreMade\" value=\"0\" class=\"sni\" /><br>\n	    Hang Attempted: <input id=\"matchTelHangAtmp\" value=\"0\" class=\"sni\" /><br>\n	    Hang Completed: <input type=\"checkbox\" id=\"matchTelHangMade\" /><br>\n	  </div>\n	  <div class=\"clear\"> </div>\n	  <div class=\"grid_9 alpha omega\">\n	    Notes:<br>\n	    <input id=\"matchNotes\" style=\"width:100%\" />\n	  </div>\n	  <div class=\"clear\"> </div>\n	  <div class=\"grid_9 alpha omega\">\n	    Penalties: <ul id=\"matchPenalities\">\n	      <li><input class=\"Penal\"/><li>\n	    </ul>\n	    <button id=\"matchAddPenalities\">+</button>\n	  </div>\n	  <center><button id=\"matchSave\">Save</button></center>\n	</div>\n      </div>\n      <div class=\"clear\"> </div>\n      <div class=\"grid_12\">\n	<!-- footer -->\n	<center style=\"color: #FFFFFF;\"><a href=\"http://camsrobotics.org/\" style=\"color: #FFFFFF;\">Nerd Herd</a> | <a href=\"http://matthewfl.com\" style=\"color: #FFFFFF;\">Made by: Matthew Francis-Landau</a> | <a href=\"http://github.com/matthewfl/FRC-Scouting\" style=\"color: #FFFFFF;\">Scouting</a></center>\n      </div>\n    </div>\n  </body>\n</html>\n\n\n" ;